mapper.select:
select study_no
from study_category
where study_name = #{studyname}

select sg_status
from sgroup
where sg_no = #{sgno}

select count(*)
from ap_sgroup
where rsg_no = to_number(#{recno}) and id = #{id}

select count(*)
from ap_sgroup
where asg_status = 'Y' and sg_no = #{sgno}

select msg_num
from m_sgroup
where sg_no = #{sgNo}

mapper.update:
update sgroup
set sg_goal=#{sgGoal}, sg_content=#{sgContent}, branch_no=#{branchNo}, sg_status='Y'
where sg_no=#{sgNo}

update m_sgroup
set msg_num=#{msgNum}, msg_rule1=#{msgRule1}, msg_rule2=#{msgRule2}, msg_rule3=#{msgRule3}, msg_met_rule=#{msgMetRule}
where sg_no=#{sgNo}
